import React, {useState} from "react";
import "./styles.css";
import Card from "./components/Card";
import cards from "./cards";
import Detailmodal from "./components/Card"



function createCard(cards) {
  return (
    <Card
      key={cards.id}
      title={cards.title}
      name={cards.name}
      img={cards.img}
      details={cards.details}
    />
  );
}

function createDetails(cards) {
  return (
    <Detailmodal
      key={cards.id}
      title={cards.title}
      details={cards.details}
    />
  );
}

