//   return arr1;

//   // console.log("After shuffling: ", arr1) 

// } 



function Shuffle() {
  function handleClick() {
    Firstcards();
    } 

  return (
    <button onClick={handleClick}>
      Click me
    </button>
  );
}


function App() {
  return (
  <div className="centered-wrapper">
  <div className="centered-content">
  <Firstcards />
  <Shuffle />
</div>
  </div>)
}

export default App;
