import React from "react";
import "./styles.css";
import Card from "./components/Card";
import cards, { shuffleButton, shuffleArray } from "./cards";


function createCard(cards) {
  return (
    <Card
      key={cards.id}
      title={cards.title}
      name={cards.name}
      img={cards.img}
      details={cards.details}
      source={cards.source}
    />
  );
}




function App() {
  return (
  <div className="centered-wrapper">
  <div className="centered-content">
  {cards.map(createCard)}
  <div>    <button onClick={shuffleArray(cards)}>Shuffle</button>
</div>
  </div>
</div>)
}

export default App;
