import React from "react";
import "./styles.css";
import Card from "./components/Card";
import cards from "./cards";


function createCard(cards) {
  return (
    <Card
      key={cards.id}
      title={cards.title}
      name={cards.name}
      img={cards.img}
      details={cards.details}
      source={cards.source}
    />
  );
}

function shuffle() {
  cards.sort(() => Math.random() - 0.5);
};

const shuffleCards = shuffle();


// function shuffleButton() {
